import React, { useState, useEffect } from 'react';
import { LineChart, BarChart, AreaChart, XAxis, YAxis, Tooltip, Legend, Line, Bar, Area } from 'recharts';
import { Shield, Activity, AlertTriangle, Database, Radio, Wifi, Lock, Server } from 'lucide-react';
import { API_BASE_URL } from '../app';

const Dashboard = () => {
  const [metrics, setMetrics] = useState(null);
  const [threats, setThreats] = useState([]);
  const [anomalies, setAnomalies] = useState([]);
  const [isMonitoring, setIsMonitoring] = useState(false);

  useEffect(() => {
    const fetchData = async () => {
      try {
        // Use Promise.all to make parallel API requests
        const [metricsRes, threatsRes, anomaliesRes, statusRes] = await Promise.all([
          fetch(`${API_BASE_URL}/api/metrics`),
          fetch(`${API_BASE_URL}/api/threats`),
          fetch(`${API_BASE_URL}/api/anomalies`),
          fetch(`${API_BASE_URL}/api/status`)
        ]);
        
        const metricsData = await metricsRes.json();
        const threatsData = await threatsRes.json();
        const anomaliesData = await anomaliesRes.json();
        const statusData = await statusRes.json();
        
        setMetrics(metricsData);
        setThreats(threatsData);
        setAnomalies(anomaliesData);
        setIsMonitoring(statusData.active);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
    const interval = setInterval(fetchData, 5000);
    
    return () => clearInterval(interval);
  }, []);

  const toggleMonitoring = async () => {
    try {
      if (isMonitoring) {
        // Stop monitoring
        await fetch(`${API_BASE_URL}/api/stop_analysis`, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json'
          }
        });
      } else {
        // Start monitoring
        await fetch(`${API_BASE_URL}/api/start_analysis`, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify({
            count: 100,
            duration: 0
          })
        });
      }
      
      // Update monitoring status
      setIsMonitoring(!isMonitoring);
    } catch (error) {
      console.error('Error toggling monitoring:', error);
    }
  };

  return (
    <div className="min-h-screen bg-gray-900 text-cyan-50">
      {/* Header Section */}
      <div className="bg-gradient-to-r from-blue-900 to-purple-900 p-6 shadow-lg">
        <div className="max-w-7xl mx-auto">
          <h1 className="text-4xl font-bold text-transparent bg-clip-text bg-gradient-to-r from-cyan-400 to-blue-500">
            Network Security Dashboard
          </h1>
          <p className="text-cyan-200 mt-2">Real-time Network Analysis & Threat Detection</p>
        </div>
      </div>

      {/* Main Content */}
      <div className="max-w-7xl mx-auto px-4 py-8">
        {/* Monitoring Control */}
        <div className="flex justify-end mb-6">
          <button
            onClick={toggleMonitoring}
            className={`flex items-center px-4 py-2 rounded-md text-white ${
              isMonitoring ? 'bg-red-600 hover:bg-red-700' : 'bg-green-600 hover:bg-green-700'
            }`}
          >
            {isMonitoring ? 'Stop Monitoring' : 'Start Monitoring'}
          </button>
        </div>
      
        {/* Stats Grid */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
          <StatCard
            icon={<Activity className="w-6 h-6 text-cyan-400" />}
            title="Network Traffic"
            value={metrics?.traffic_rate || '0'}
            unit="pkts/s"
            trend={+15}
          />
          <StatCard
            icon={<AlertTriangle className="w-6 h-6 text-red-400" />}
            title="Active Threats"
            value={threats.length}
            unit="threats"
            trend={-2}
            trendColor="red"
          />
          <StatCard
            icon={<Radio className="w-6 h-6 text-green-400" />}
            title="Response Time"
            value={metrics?.avg_response_time || '0'}
            unit="ms"
            trend={-5}
          />
          <StatCard
            icon={<Shield className="w-6 h-6 text-purple-400" />}
            title="Security Score"
            value={metrics?.security_score || '0'}
            unit="%"
            trend={+3}
          />
        </div>

        {/* Charts Grid */}
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6 mb-8">
          <ChartCard title="Network Traffic Analysis">
            <AreaChart width={600} height={300} data={metrics?.traffic_history || []}>
              <defs>
                <linearGradient id="colorTraffic" x1="0" y1="0" x2="0" y2="1">
                  <stop offset="5%" stopColor="#06b6d4" stopOpacity={0.8}/>
                  <stop offset="95%" stopColor="#06b6d4" stopOpacity={0}/>
                </linearGradient>
              </defs>
              <XAxis dataKey="timestamp" />
              <YAxis />
              <Tooltip />
              <Area type="monotone" dataKey="value" stroke="#06b6d4" fillOpacity={1} fill="url(#colorTraffic)" />
            </AreaChart>
          </ChartCard>

          <ChartCard title="Threat Distribution">
            <BarChart width={600} height={300} data={metrics?.threat_distribution || []}>
              <XAxis dataKey="type" />
              <YAxis />
              <Tooltip />
              <Bar dataKey="count" fill="#7e22ce" />
            </BarChart>
          </ChartCard>
        </div>

        {/* Threat Feed */}
        <div className="bg-gray-800 rounded-lg p-6 mb-8">
          <h3 className="text-xl font-bold mb-4 text-cyan-400">Live Threat Feed</h3>
          {threats.length > 0 ? (
            <div className="space-y-4">
              {threats.map((threat, index) => (
                <ThreatAlert
                  key={index}
                  type={threat.type}
                  source={threat.source_ip}
                  severity={threat.severity}
                  timestamp={threat.timestamp}
                  details={threat.details}
                />
              ))}
            </div>
          ) : (
            <div className="text-center p-8 text-gray-500">
              <AlertTriangle className="w-12 h-12 mx-auto mb-4 opacity-30" />
              <p>No threats detected</p>
            </div>
          )}
        </div>

        {/* Anomalies Section */}
        {anomalies && anomalies.length > 0 && (
          <div className="bg-gray-800 rounded-lg p-6">
            <h3 className="text-xl font-bold mb-4 text-yellow-400">Detected Anomalies</h3>
            <div className="space-y-4">
              {anomalies.map((anomaly, index) => (
                <div key={index} className="bg-gray-900 border border-yellow-900 rounded-lg p-4">
                  <div className="flex justify-between items-start">
                    <h4 className="font-medium text-yellow-400">{anomaly.type}</h4>
                    <span className="text-xs px-2 py-1 bg-yellow-900 text-yellow-300 rounded-full">
                      {anomaly.severity}
                    </span>
                  </div>
                  <p className="mt-2 text-sm">{anomaly.details}</p>
                  <p className="text-xs text-gray-500 mt-1">Detected: {new Date(anomaly.timestamp).toLocaleString()}</p>
                </div>
              ))}
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

const StatCard = ({ icon, title, value, unit, trend, trendColor = 'cyan' }) => (
  <div className="bg-gray-800 rounded-lg p-6 border border-gray-700 hover:border-cyan-500 transition-all duration-300">
    <div className="flex justify-between items-start">
      <div>
        <p className="text-gray-400 text-sm">{title}</p>
        <p className="text-2xl font-bold mt-2">
          {value} <span className="text-sm text-gray-400">{unit}</span>
        </p>
      </div>
      {icon}
    </div>
    <div className={`mt-2 text-sm text-${trendColor}-400`}>
      {trend > 0 ? '↑' : '↓'} {Math.abs(trend)}%
    </div>
  </div>
);

const ChartCard = ({ title, children }) => (
  <div className="bg-gray-800 rounded-lg p-6 border border-gray-700">
    <h3 className="text-xl font-bold mb-4 text-cyan-400">{title}</h3>
    <div className="overflow-hidden">{children}</div>
  </div>
);

const ThreatAlert = ({ type, source, severity, timestamp, details }) => (
  <div className="flex items-center space-x-4 p-4 bg-gray-900 rounded-lg border border-red-900">
    <AlertTriangle className="w-6 h-6 text-red-400" />
    <div className="flex-1">
      <div className="flex justify-between">
        <p className="font-semibold text-red-400">{type}</p>
        <span className={`text-xs px-2 py-1 rounded-full ${
          severity === 'Critical' ? 'bg-red-900 text-red-200' :
          severity === 'High' ? 'bg-orange-900 text-orange-200' :
          severity === 'Medium' ? 'bg-yellow-900 text-yellow-200' :
          'bg-blue-900 text-blue-200'
        }`}>
          {severity}
        </span>
      </div>
      <p className="text-sm text-gray-400">
        Source: {source} | {new Date(timestamp).toLocaleString()}
      </p>
      {details && <p className="text-sm mt-1">{details}</p>}
    </div>
  </div>
);

export default Dashboard;
