import React, { useState, useEffect } from 'react';
import { LineChart, BarChart, AreaChart, XAxis, YAxis, Tooltip, Legend, Line, Bar, Area, ResponsiveContainer } from 'recharts';
import { Shield, Activity, AlertTriangle, Database, Radio, Wifi, Lock, Server, Cpu, BarChart2, Play, Pause, RefreshCw } from 'lucide-react';
import { API_BASE_URL } from '../app';

const Dashboard = () => {
  const [metrics, setMetrics] = useState(null);
  const [threats, setThreats] = useState([]);
  const [anomalies, setAnomalies] = useState([]);
  const [isMonitoring, setIsMonitoring] = useState(false);
  const [isLoading, setIsLoading] = useState(true);
  const [chartTimeframe, setChartTimeframe] = useState('hour'); // hour, day, week

  // Mock data for initial display
  const mockData = {
    traffic_rate: 125,
    avg_response_time: 52,
    security_score: 87,
    active_threats: 0,
    traffic_history: Array.from({ length: 24 }, (_, i) => ({
      timestamp: `${i}:00`,
      value: Math.floor(Math.random() * 100) + 50
    })),
    threat_distribution: [
      { type: 'Intrusion', count: 12 },
      { type: 'DDoS', count: 5 },
      { type: 'Malware', count: 8 },
      { type: 'Phishing', count: 15 }
    ]
  };

  useEffect(() => {
    fetchData();
    // Set up interval for periodic updates
    const interval = setInterval(fetchData, 10000);
    return () => clearInterval(interval);
  }, []);

  const fetchData = async () => {
    setIsLoading(true);
    try {
      // Fetch data from API
      const [metricsRes, threatsRes, anomaliesRes, statusRes] = await Promise.all([
        fetch(`${API_BASE_URL}/api/metrics`),
        fetch(`${API_BASE_URL}/api/threats`),
        fetch(`${API_BASE_URL}/api/anomalies`),
        fetch(`${API_BASE_URL}/api/status`)
      ]);
      
      // Process responses
      if (metricsRes.ok) {
        const metricsData = await metricsRes.json();
        setMetrics(metricsData);
      } else {
        // Fallback to mock data if API fails
        setMetrics(mockData);
      }
      
      if (threatsRes.ok) {
        const threatsData = await threatsRes.json();
        setThreats(threatsData);
      } else {
        setThreats([]);
      }
      
      if (anomaliesRes.ok) {
        const anomaliesData = await anomaliesRes.json();
        setAnomalies(anomaliesData);
      } else {
        setAnomalies([]);
      }
      
      if (statusRes.ok) {
        const statusData = await statusRes.json();
        setIsMonitoring(statusData.active);
      }
    } catch (error) {
      console.error('Error fetching dashboard data:', error);
      // Fallback to mock data
      setMetrics(mockData);
    }
    setIsLoading(false);
  };

  const toggleMonitoring = async () => {
    try {
      const endpoint = isMonitoring ? '/api/stop_analysis' : '/api/start_analysis';
      
      const res = await fetch(`${API_BASE_URL}${endpoint}`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          count: 100,
          duration: 0
        })
      });
      
      if (res.ok) {
        setIsMonitoring(!isMonitoring);
        
        // Refresh data after toggling monitoring
        fetchData();
      }
    } catch (error) {
      console.error('Error toggling monitoring:', error);
    }
  };

  // Format numbers for display
  const formatNumber = (num) => {
    if (num >= 1000) {
      return (num / 1000).toFixed(1) + 'k';
    }
    return num;
  };

  return (
    <div className="min-h-screen bg-gray-900 text-white pb-16">
      {/* Header Banner */}
      <div className="bg-gradient-to-r from-blue-900 to-indigo-900 shadow-xl">
        <div className="container mx-auto px-4 py-8 sm:px-6 lg:px-8">
          <div className="flex flex-col md:flex-row md:items-center md:justify-between">
            <div>
              <h1 className="text-3xl md:text-4xl font-bold text-white mb-2">
                Network Security Dashboard
              </h1>
              <p className="text-blue-200">
                Real-time Network Analysis & Threat Detection
              </p>
            </div>
            <div className="mt-4 md:mt-0 flex items-center">
              <div className="flex items-center mr-6">
                <div className={`w-3 h-3 rounded-full mr-2 ${isMonitoring ? 'bg-green-500 animate-pulse' : 'bg-red-500'}`}></div>
                <span className={`text-sm font-medium ${isMonitoring ? 'text-green-400' : 'text-red-400'}`}>
                  {isMonitoring ? 'Monitoring Active' : 'Monitoring Inactive'}
                </span>
              </div>
              <button
                onClick={toggleMonitoring}
                className={`flex items-center justify-center px-4 py-2 rounded-md text-sm font-medium transition-colors ${
                  isMonitoring 
                    ? 'bg-red-600 hover:bg-red-700 text-white' 
                    : 'bg-green-600 hover:bg-green-700 text-white'
                }`}
              >
                {isMonitoring ? (
                  <>
                    <Pause className="w-4 h-4 mr-2" />
                    Stop
                  </>
                ) : (
                  <>
                    <Play className="w-4 h-4 mr-2" />
                    Start Monitoring
                  </>
                )}
              </button>
              <button
                onClick={fetchData}
                className="ml-2 p-2 rounded-full bg-blue-800 hover:bg-blue-700 transition-colors"
                title="Refresh Data"
              >
                <RefreshCw className={`w-5 h-5 text-blue-200 ${isLoading ? 'animate-spin' : ''}`} />
              </button>
            </div>
          </div>
        </div>
      </div>

      <div className="container mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Stats Overview */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
          <StatCard
            title="Network Traffic"
            value={metrics?.traffic_rate || '0'}
            unit="pkts/s"
            icon={<Activity className="w-8 h-8 text-cyan-400" />}
            trend={+15}
            trendLabel="from last hour"
            isLoading={isLoading}
            bgColor="from-cyan-900/50 to-blue-900/30"
            hoverColor="cyan"
          />
          <StatCard
            title="Active Threats"
            value={threats?.length || '0'}
            unit="threats"
            icon={<AlertTriangle className="w-8 h-8 text-red-400" />}
            trend={-2}
            trendLabel="from yesterday"
            trendColor={threats?.length > 0 ? "red" : "green"}
            isLoading={isLoading}
            bgColor="from-red-900/50 to-red-900/20"
            hoverColor="red"
          />
          <StatCard
            title="Response Time"
            value={metrics?.avg_response_time || '0'}
            unit="ms"
            icon={<Radio className="w-8 h-8 text-green-400" />}
            trend={-5}
            trendLabel="vs. baseline"
            isLoading={isLoading}
            bgColor="from-green-900/40 to-green-900/20"
            hoverColor="green"
          />
          <StatCard
            title="Security Score"
            value={metrics?.security_score || '0'}
            unit="%"
            icon={<Shield className="w-8 h-8 text-purple-400" />}
            trend={+3}
            trendLabel="since last scan"
            isLoading={isLoading}
            bgColor="from-purple-900/40 to-purple-900/20"
            hoverColor="purple"
          />
        </div>

        {/* Charts Section */}
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6 mb-8">
          {/* Traffic Analysis Chart */}
          <div className="bg-gray-800 rounded-lg p-6 border border-gray-700 hover:border-cyan-800 transition-all shadow-lg">
            <div className="flex justify-between items-center mb-6">
              <h3 className="text-xl font-bold text-cyan-400 flex items-center">
                <BarChart2 className="w-5 h-5 mr-2" />
                Network Traffic Analysis
              </h3>
              <div className="flex space-x-1">
                {['hour', 'day', 'week'].map(period => (
                  <button
                    key={period}
                    onClick={() => setChartTimeframe(period)}
                    className={`px-3 py-1 text-xs rounded ${
                      chartTimeframe === period 
                        ? 'bg-cyan-700 text-white' 
                        : 'bg-gray-700 text-gray-300 hover:bg-gray-600'
                    }`}
                  >
                    {period.charAt(0).toUpperCase() + period.slice(1)}
                  </button>
                ))}
              </div>
            </div>
            {isLoading ? (
              <div className="h-64 flex items-center justify-center">
                <div className="w-10 h-10 border-4 border-cyan-500 border-t-transparent rounded-full animate-spin"></div>
              </div>
            ) : (
              <div className="h-64">
                <ResponsiveContainer width="100%" height="100%">
                  <AreaChart data={metrics?.traffic_history || []}>
                    <defs>
                      <linearGradient id="colorTraffic" x1="0" y1="0" x2="0" y2="1">
                        <stop offset="5%" stopColor="#06b6d4" stopOpacity={0.8}/>
                        <stop offset="95%" stopColor="#06b6d4" stopOpacity={0}/>
                      </linearGradient>
                    </defs>
                    <XAxis 
                      dataKey="timestamp" 
                      stroke="#94a3b8"
                      tick={{ fill: '#94a3b8', fontSize: 12 }}
                    />
                    <YAxis 
                      stroke="#94a3b8"
                      tick={{ fill: '#94a3b8', fontSize: 12 }}
                    />
                    <Tooltip 
                      contentStyle={{ 
                        backgroundColor: '#1e293b', 
                        borderColor: '#475569',
                        color: '#f8fafc' 
                      }}
                      itemStyle={{ color: '#f8fafc' }}
                      labelStyle={{ color: '#94a3b8' }}
                    />
                    <Area 
                      type="monotone" 
                      dataKey="value" 
                      stroke="#06b6d4" 
                      strokeWidth={2}
                      fillOpacity={1} 
                      fill="url(#colorTraffic)" 
                    />
                  </AreaChart>
                </ResponsiveContainer>
              </div>
            )}
          </div>

          {/* Threat Distribution Chart */}
          <div className="bg-gray-800 rounded-lg p-6 border border-gray-700 hover:border-purple-800 transition-all shadow-lg">
            <h3 className="text-xl font-bold mb-6 text-purple-400 flex items-center">
              <Shield className="w-5 h-5 mr-2" />
              Threat Distribution
            </h3>
            {isLoading ? (
              <div className="h-64 flex items-center justify-center">
                <div className="w-10 h-10 border-4 border-purple-500 border-t-transparent rounded-full animate-spin"></div>
              </div>
            ) : (
              <div className="h-64">
                <ResponsiveContainer width="100%" height="100%">
                  <BarChart data={metrics?.threat_distribution || []}>
                    <XAxis 
                      dataKey="type" 
                      stroke="#94a3b8"
                      tick={{ fill: '#94a3b8', fontSize: 12 }}
                    />
                    <YAxis 
                      stroke="#94a3b8"
                      tick={{ fill: '#94a3b8', fontSize: 12 }}
                    />
                    <Tooltip 
                      contentStyle={{ 
                        backgroundColor: '#1e293b', 
                        borderColor: '#475569',
                        color: '#f8fafc' 
                      }}
                      itemStyle={{ color: '#f8fafc' }}
                      labelStyle={{ color: '#94a3b8' }}
                    />
                    <Bar 
                      dataKey="count" 
                      fill="#9333ea" 
                      radius={[4, 4, 0, 0]}
                    />
                  </BarChart>
                </ResponsiveContainer>
              </div>
            )}
          </div>
        </div>

        {/* Threat Feed */}
        <div className="bg-gray-800 rounded-lg shadow-lg border border-gray-700 mb-8">
          <div className="px-6 py-4 border-b border-gray-700">
            <h3 className="text-xl font-bold text-red-400 flex items-center">
              <AlertTriangle className="w-5 h-5 mr-2" />
              Live Threat Feed
            </h3>
          </div>
          <div className="p-6">
            {isLoading ? (
              <div className="flex justify-center items-center h-32">
                <div className="w-10 h-10 border-4 border-red-500 border-t-transparent rounded-full animate-spin"></div>
              </div>
            ) : threats.length > 0 ? (
              <div className="space-y-4">
                {threats.map((threat, index) => (
                  <ThreatAlert
                    key={index}
                    type={threat.type}
                    source={threat.source_ip}
                    severity={threat.severity}
                    timestamp={threat.timestamp}
                    details={threat.details}
                  />
                ))}
              </div>
            ) : (
              <div className="flex flex-col items-center justify-center py-12 text-center">
                <Shield className="w-16 h-16 text-green-500 mb-4" />
                <h4 className="text-xl font-semibold text-green-400 mb-2">No active threats detected</h4>
                <p className="text-gray-400 max-w-md">
                  Your network is currently secure. Continuous monitoring is active to detect any potential threats.
                </p>
              </div>
            )}
          </div>
        </div>

        {/* System Health and Performance Metrics */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
          <HealthMetricCard
            title="CPU Usage"
            value={Math.floor(Math.random() * 30) + 10}
            icon={<Cpu />}
            color="green"
            isLoading={isLoading}
          />
          <HealthMetricCard
            title="Memory Usage"
            value={Math.floor(Math.random() * 40) + 30}
            icon={<Database />}
            color="blue"
            isLoading={isLoading}
          />
          <HealthMetricCard
            title="Disk I/O"
            value={Math.floor(Math.random() * 20) + 5}
            icon={<Server />}
            color="purple"
            isLoading={isLoading}
          />
        </div>
      </div>
    </div>
  );
};

const StatCard = ({ 
  title, 
  value, 
  unit, 
  icon, 
  trend = 0, 
  trendColor = 'cyan',
  trendLabel = 'change',
  isLoading = false,
  bgColor = 'from-blue-900/30 to-gray-800',
  hoverColor = 'blue'
}) => {
  const getTrendColor = () => {
    if (trendColor === 'auto') {
      return trend >= 0 ? 'text-green-400' : 'text-red-400';
    }
    const colorMap = {
      cyan: 'text-cyan-400',
      green: 'text-green-400',
      red: 'text-red-400',
      purple: 'text-purple-400',
      yellow: 'text-yellow-400'
    };
    return colorMap[trendColor] || 'text-cyan-400';
  };

  const getHoverBorder = () => {
    const colorMap = {
      cyan: 'hover:border-cyan-500',
      green: 'hover:border-green-500',
      red: 'hover:border-red-500',
      purple: 'hover:border-purple-500',
      yellow: 'hover:border-yellow-500'
    };
    return colorMap[hoverColor] || 'hover:border-cyan-500';
  };

  return (
    <div className={`relative overflow-hidden rounded-lg border border-gray-700 ${getHoverBorder()} transition-all duration-300 shadow-lg bg-gradient-to-br ${bgColor}`}>
      <div className="p-6">
        <div className="flex justify-between items-start">
          <div>
            <p className="text-gray-400 text-sm">{title}</p>
            {isLoading ? (
              <div className="mt-2 h-8 w-20 bg-gray-700 rounded animate-pulse"></div>
            ) : (
              <p className="text-3xl font-bold mt-2">
                {value} <span className="text-sm text-gray-400">{unit}</span>
              </p>
            )}
          </div>
          <div className="p-2 rounded-md bg-gray-800/50">
            {icon}
          </div>
        </div>
        {!isLoading && (
          <div className={`mt-4 text-sm flex items-center ${getTrendColor()}`}>
            {trend > 0 ? '↑' : '↓'} {Math.abs(trend)}% {trendLabel}
          </div>
        )}
      </div>
    </div>
  );
};

const ThreatAlert = ({ type, source, severity, timestamp, details }) => {
  const [expanded, setExpanded] = useState(false);
  
  const getSeverityColor = (severity) => {
    switch (severity?.toLowerCase()) {
      case 'critical': return 'bg-red-900 border-red-600 text-red-200';
      case 'high': return 'bg-orange-900 border-orange-600 text-orange-200';
      case 'medium': return 'bg-yellow-900 border-yellow-600 text-yellow-200';
      case 'low': return 'bg-blue-900 border-blue-600 text-blue-200';
      default: return 'bg-gray-800 border-gray-600 text-gray-200';
    }
  };
  
  const formatDate = (timestamp) => {
    if (!timestamp) return 'Unknown';
    try {
      return new Date(timestamp).toLocaleString();
    } catch (e) {
      return timestamp;
    }
  };

  return (
    <div 
      className={`${getSeverityColor(severity)} rounded-lg border p-4 transition-all duration-200 cursor-pointer hover:opacity-90`}
      onClick={() => setExpanded(!expanded)}
    >
      <div className="flex items-start">
        <AlertTriangle className="w-6 h-6 mr-4 mt-1 flex-shrink-0" />
        <div className="flex-1">
          <div className="flex justify-between items-start">
            <h4 className="font-bold text-lg">{type}</h4>
            <span className="px-2.5 py-0.5 bg-black/20 rounded-full text-xs font-medium">
              {severity}
            </span>
          </div>
          <div className="text-sm mt-1 grid grid-cols-1 md:grid-cols-2 gap-1">
            <p>Source: <span className="font-mono">{source}</span></p>
            <p>Time: {formatDate(timestamp)}</p>
          </div>
          {expanded && (
            <div className="mt-3 p-2 bg-black/20 rounded-md text-sm">
              <p>{details || 'No additional details available.'}</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

const HealthMetricCard = ({ title, value, icon, color = 'green', isLoading = false }) => {
  const getColorClasses = () => {
    const colors = {
      green: {
        bg: 'bg-green-500',
        text: 'text-green-500',
        light: 'text-green-400'
      },
      blue: {
        bg: 'bg-blue-500',
        text: 'text-blue-500',
        light: 'text-blue-400'
      },
      red: {
        bg: 'bg-red-500',
        text: 'text-red-500',
        light: 'text-red-400'
      },
      purple: {
        bg: 'bg-purple-500',
        text: 'text-purple-500',
        light: 'text-purple-400'
      },
      yellow: {
        bg: 'bg-yellow-500',
        text: 'text-yellow-500',
        light: 'text-yellow-400'
      }
    };
    return colors[color] || colors.green;
  };

  const colorClasses = getColorClasses();
  const getProgressWidth = () => `${value}%`;
  const getHealthStatus = () => {
    if (value < 30) return 'Healthy';
    if (value < 70) return 'Normal';
    return 'High';
  };

  return (
    <div className="bg-gray-800 rounded-lg p-6 border border-gray-700 hover:border-gray-600 transition-all shadow-lg">
      <div className="flex items-center mb-4">
        <div className={`p-2 rounded-md ${colorClasses.bg} bg-opacity-20 mr-3`}>
          <div className={colorClasses.text}>
            {icon}
          </div>
        </div>
        <h3 className="text-lg font-medium text-white">{title}</h3>
      </div>
      
      {isLoading ? (
        <div className="h-2 w-full bg-gray-700 rounded animate-pulse"></div>
      ) : (
        <>
          <div className="w-full bg-gray-700 rounded-full h-2.5 mb-4">
            <div 
              className={`${colorClasses.bg} h-2.5 rounded-full`} 
              style={{ width: getProgressWidth() }}
            ></div>
          </div>
          
          <div className="flex justify-between items-center">
            <div className={colorClasses.light}>
              {getHealthStatus()}
            </div>
            <div className="text-2xl font-bold text-white">
              {value}%
            </div>
          </div>
        </>
      )}
    </div>
  );
};

export default Dashboard;
