import React, { useState, useEffect } from 'react';
import { LineChart, BarChart, XAxis, YAxis, Tooltip, Legend, Line, Bar, CartesianGrid, ResponsiveContainer } from 'recharts';
import { BarChart2, Clock, Activity, TrendingUp, Filter, ArrowUpRight, ArrowDownRight } from 'lucide-react';

const Analysis = () => {
  const [trafficData, setTrafficData] = useState([]);
  const [responseTimeData, setResponseTimeData] = useState([]);
  const [statusCodeData, setStatusCodeData] = useState([]);
  const [anomalyData, setAnomalyData] = useState([]);
  const [timeFrame, setTimeFrame] = useState('24h');
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    const fetchAnalyticsData = async () => {
      setIsLoading(true);
      try {
        const [trafficRes, responseTimeRes, statusCodeRes, anomalyRes] = await Promise.all([
          fetch(`/api/analytics/traffic?timeframe=${timeFrame}`),
          fetch(`/api/analytics/response-time?timeframe=${timeFrame}`),
          fetch(`/api/analytics/status-codes?timeframe=${timeFrame}`),
          fetch(`/api/analytics/anomalies?timeframe=${timeFrame}`)
        ]);
        
        setTrafficData(await trafficRes.json());
        setResponseTimeData(await responseTimeRes.json());
        setStatusCodeData(await statusCodeRes.json());
        setAnomalyData(await anomalyRes.json());
      } catch (error) {
        console.error('Error fetching analytics data:', error);
      } finally {
        setIsLoading(false);
      }
    };

    fetchAnalyticsData();
  }, [timeFrame]);

  const StatCard = ({ icon, title, value, change, positive = true }) => (
    <div className="bg-gray-800 rounded-lg p-6 border border-gray-700">
      <div className="flex justify-between mb-4">
        <div>
          <h3 className="text-gray-400 text-sm">{title}</h3>
          <p className="text-2xl font-bold text-white mt-1">{value}</p>
        </div>
        <div className="bg-gray-900 p-3 rounded-lg">
          {icon}
        </div>
      </div>
      <div className={`flex items-center text-sm ${positive ? 'text-green-400' : 'text-red-400'}`}>
        {positive ? <ArrowUpRight className="w-4 h-4 mr-1" /> : <ArrowDownRight className="w-4 h-4 mr-1" />}
        <span>{change}% from previous period</span>
      </div>
    </div>
  );

  return (
    <div className="min-h-screen bg-gray-900 text-cyan-50 pl-64">
      <div className="p-8">
        <div className="flex justify-between items-center mb-8">
          <div>
            <h1 className="text-3xl font-bold text-cyan-400">Traffic Analysis</h1>
            <p className="text-gray-400 mt-1">Detailed metrics and pattern analysis</p>
          </div>
          <div className="flex bg-gray-800 rounded-lg p-1">
            <button
              className={`px-4 py-2 rounded ${timeFrame === '24h' ? 'bg-blue-900 text-cyan-300' : 'text-gray-400'}`}
              onClick={() => setTimeFrame('24h')}
            >
              24h
            </button>
            <button
              className={`px-4 py-2 rounded ${timeFrame === '7d' ? 'bg-blue-900 text-cyan-300' : 'text-gray-400'}`}
              onClick={() => setTimeFrame('7d')}
            >
              7d
            </button>
            <button
              className={`px-4 py-2 rounded ${timeFrame === '30d' ? 'bg-blue-900 text-cyan-300' : 'text-gray-400'}`}
              onClick={() => setTimeFrame('30d')}
            >
              30d
            </button>
          </div>
        </div>

        {/* Stats Row */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
          <StatCard 
            icon={<Activity className="w-6 h-6 text-cyan-400" />}
            title="Avg Traffic Rate"
            value="3,245 req/min"
            change="12.5"
            positive={true}
          />
          <StatCard 
            icon={<Clock className="w-6 h-6 text-green-400" />}
            title="Avg Response Time"
            value="121 ms"
            change="5.2"
            positive={false}
          />
          <StatCard 
            icon={<TrendingUp className="w-6 h-6 text-purple-400" />}
            title="Peak Hours Traffic"
            value="5,721 req/min"
            change="8.7"
            positive={true}
          />
          <StatCard 
            icon={<BarChart2 className="w-6 h-6 text-yellow-400" />}
            title="Unique IPs"
            value="2,891"
            change="23.4"
            positive={true}
          />
        </div>

        {/* Charts Grid */}
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8 mb-8">
          <div className="bg-gray-800 rounded-lg p-6 border border-gray-700">
            <h3 className="text-xl font-semibold text-cyan-400 mb-4">Traffic Trend Analysis</h3>
            <ResponsiveContainer width="100%" height={300}>
              <LineChart data={trafficData}>
                <CartesianGrid strokeDasharray="3 3" stroke="#374151" />
                <XAxis dataKey="timestamp" stroke="#9CA3AF" />
                <YAxis stroke="#9CA3AF" />
                <Tooltip contentStyle={{ backgroundColor: '#1F2937', borderColor: '#374151' }} />
                <Legend />
                <Line type="monotone" dataKey="requests" stroke="#06b6d4" strokeWidth={2} dot={false} />
                <Line type="monotone" dataKey="unique_visitors" stroke="#8b5cf6" strokeWidth={2} dot={false} />
              </LineChart>
            </ResponsiveContainer>
          </div>

          <div className="bg-gray-800 rounded-lg p-6 border border-gray-700">
            <h3 className="text-xl font-semibold text-cyan-400 mb-4">Response Time Distribution</h3>
            <ResponsiveContainer width="100%" height={300}>
              <LineChart data={responseTimeData}>
                <CartesianGrid strokeDasharray="3 3" stroke="#374151" />
                <XAxis dataKey="timestamp" stroke="#9CA3AF" />
                <YAxis stroke="#9CA3AF" />
                <Tooltip contentStyle={{ backgroundColor: '#1F2937', borderColor: '#374151' }} />
                <Legend />
                <Line type="monotone" dataKey="avg_response_time" stroke="#10b981" strokeWidth={2} dot={false} />
                <Line type="monotone" dataKey="p95_response_time" stroke="#f59e0b" strokeWidth={2} dot={false} />
              </LineChart>
            </ResponsiveContainer>
          </div>
        </div>

        {/* Bottom Row Charts */}
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
          <div className="bg-gray-800 rounded-lg p-6 border border-gray-700">
            <h3 className="text-xl font-semibold text-cyan-400 mb-4">HTTP Status Code Distribution</h3>
            <ResponsiveContainer width="100%" height={300}>
              <BarChart data={statusCodeData}>
                <CartesianGrid strokeDasharray="3 3" stroke="#374151" />
                <XAxis dataKey="status_code" stroke="#9CA3AF" />
                <YAxis stroke="#9CA3AF" />
                <Tooltip contentStyle={{ backgroundColor: '#1F2937', borderColor: '#374151' }} />
                <Bar dataKey="count" fill="#7e22ce" />
              </BarChart>
            </ResponsiveContainer>
          </div>

          <div className="bg-gray-800 rounded-lg p-6 border border-gray-700">
            <h3 className="text-xl font-semibold text-cyan-400 mb-4">Anomaly Detection Timeline</h3>
            <div className="mb-4 flex items-center justify-between">
              <div className="text-sm text-gray-400">
                <span className="inline-block w-3 h-3 bg-red-500 rounded-full mr-1"></span> Critical
                <span className="inline-block w-3 h-3 bg-yellow-500 rounded-full ml-4 mr-1"></span> Warning
                <span className="inline-block w-3 h-3 bg-blue-500 rounded-full ml-4 mr-1"></span> Info
              </div>
              <div className="flex items-center">
                <Filter className="w-4 h-4 text-gray-400 mr-1" />
                <span className="text-sm text-gray-400">Filter</span>
              </div>
            </div>
            <ResponsiveContainer width="100%" height={250}>
              <LineChart data={anomalyData}>
                <CartesianGrid strokeDasharray="3 3" stroke="#374151" />
                <XAxis dataKey="timestamp" stroke="#9CA3AF" />
                <YAxis stroke="#9CA3AF" />
                <Tooltip contentStyle={{ backgroundColor: '#1F2937', borderColor: '#374151' }} />
                <Legend />
                <Line type="monotone" dataKey="anomaly_score" stroke="#ef4444" strokeWidth={2} dot={true} />
                <Line type="monotone" dataKey="threshold" stroke="#a855f7" strokeWidth={1} dot={false} strokeDasharray="5 5" />
              </LineChart>
            </ResponsiveContainer>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Analysis;
