import React, { useState, useEffect } from 'react';
import { Link, useLocation } from 'react-router-dom';
import { Home, Activity, Shield, Settings, Menu, X, ChevronDown, Server, Bell, User, LogOut } from 'lucide-react';

const Navbar = ({ isOpen, setIsOpen }) => {
  const [isSettingsOpen, setIsSettingsOpen] = useState(false);
  const [notifications, setNotifications] = useState([
    { id: 1, message: "New threat detected", time: "2 min ago", read: false },
    { id: 2, message: "Security scan completed", time: "1 hour ago", read: false },
    { id: 3, message: "System update available", time: "3 hours ago", read: true }
  ]);
  const [showNotifications, setShowNotifications] = useState(false);
  const [unreadCount, setUnreadCount] = useState(0);
  const location = useLocation();

  // Calculate unread notification count
  useEffect(() => {
    const count = notifications.filter(n => !n.read).length;
    setUnreadCount(count);
  }, [notifications]);

  const isActive = (path) => {
    return location.pathname === path;
  };

  const markAllAsRead = () => {
    setNotifications(notifications.map(n => ({ ...n, read: true })));
  };

  const markAsRead = (id) => {
    setNotifications(notifications.map(n => 
      n.id === id ? { ...n, read: true } : n
    ));
  };

  const menuItems = [
    {
      name: 'Dashboard',
      path: '/dashboard',
      icon: <Home className="w-5 h-5" />
    },
    {
      name: 'Analysis',
      path: '/analysis',
      icon: <Activity className="w-5 h-5" />
    },
    {
      name: 'Security',
      path: '/security',
      icon: <Shield className="w-5 h-5" />
    }
  ];

  return (
    <>
      {/* Mobile menu button - visible on small screens */}
      <div className="fixed top-0 left-0 z-40 w-full bg-gray-900 md:hidden">
        <div className="flex items-center justify-between p-4">
          <div className="flex items-center">
            <Server className="w-6 h-6 mr-2 text-cyan-400" />
            <span className="text-xl font-bold text-cyan-400">NetSecMon</span>
          </div>
          <div className="flex items-center space-x-3">
            <button
              onClick={() => setShowNotifications(!showNotifications)}
              className="relative p-2 text-gray-300 rounded-md hover:bg-gray-800 focus:outline-none"
            >
              <Bell className="w-6 h-6" />
              {unreadCount > 0 && (
                <span className="absolute top-0 right-0 w-4 h-4 bg-red-500 rounded-full text-xs flex items-center justify-center">
                  {unreadCount}
                </span>
              )}
            </button>
            <button
              onClick={() => setIsOpen(!isOpen)}
              className="p-2 text-gray-300 rounded-md hover:bg-gray-800 focus:outline-none"
            >
              {isOpen ? <X className="w-6 h-6" /> : <Menu className="w-6 h-6" />}
            </button>
          </div>
        </div>
      </div>

      {/* Notifications panel (mobile) */}
      {showNotifications && (
        <div className="fixed top-16 right-0 z-40 w-full md:w-80 bg-gray-800 shadow-lg rounded-bl-lg rounded-tl-lg md:right-4 md:top-20 md:rounded-lg overflow-hidden">
          <div className="flex items-center justify-between p-4 border-b border-gray-700">
            <h3 className="font-medium">Notifications</h3>
            <button 
              onClick={markAllAsRead}
              className="text-xs text-cyan-400 hover:text-cyan-300"
            >
              Mark all as read
            </button>
          </div>
          <div className="max-h-80 overflow-y-auto">
            {notifications.length > 0 ? (
              <div>
                {notifications.map(notification => (
                  <div 
                    key={notification.id}
                    onClick={() => markAsRead(notification.id)}
                    className={`p-3 border-b border-gray-700 hover:bg-gray-700 cursor-pointer ${!notification.read ? 'bg-gray-700/50' : ''}`}
                  >
                    <div className="flex justify-between">
                      <p className={`text-sm ${!notification.read ? 'font-medium text-white' : 'text-gray-300'}`}>
                        {notification.message}
                      </p>
                      {!notification.read && (
                        <span className="w-2 h-2 bg-cyan-400 rounded-full"></span>
                      )}
                    </div>
                    <p className="text-xs text-gray-500 mt-1">{notification.time}</p>
                  </div>
                ))}
              </div>
            ) : (
              <div className="p-4 text-center text-gray-500">
                <p>No notifications</p>
              </div>
            )}
          </div>
          <div className="p-2 border-t border-gray-700 bg-gray-800/90">
            <button 
              onClick={() => setShowNotifications(false)}
              className="w-full py-2 text-sm text-center text-gray-400 hover:text-white"
            >
              Close
            </button>
          </div>
        </div>
      )}

      {/* Sidebar - hidden on mobile when collapsed, always visible on desktop */}
      <div
        className={`fixed inset-y-0 left-0 z-30 w-64 bg-gray-900 border-r border-gray-800 transition-transform duration-300 ease-in-out transform ${
          isOpen ? 'translate-x-0' : '-translate-x-full'
        } md:translate-x-0`}
      >
        <div className="flex flex-col h-full">
          {/* Logo and close button (mobile only) */}
          <div className="flex items-center justify-between p-4 border-b border-gray-800">
            <div className="flex items-center">
              <Server className="w-6 h-6 mr-2 text-cyan-400" />
              <span className="text-xl font-bold text-cyan-400">NetSecMon</span>
            </div>
            <button
              onClick={() => setIsOpen(false)}
              className="p-2 text-gray-300 rounded-md hover:bg-gray-800 focus:outline-none md:hidden"
            >
              <X className="w-5 h-5" />
            </button>
          </div>

          {/* User profile section */}
          <div className="p-4 border-b border-gray-800">
            <div className="flex items-center">
              <div className="w-10 h-10 rounded-full bg-gradient-to-r from-cyan-500 to-blue-500 flex items-center justify-center text-white font-bold">
                A
              </div>
              <div className="ml-3">
                <div className="text-sm font-medium text-white">Admin User</div>
                <div className="text-xs text-gray-400">Security Manager</div>
              </div>
            </div>
          </div>

          {/* Navigation Links */}
          <nav className="flex-1 px-2 py-4 space-y-1 overflow-y-auto">
            {menuItems.map((item) => (
              <Link
                key={item.name}
                to={item.path}
                className={`flex items-center px-4 py-3 text-sm font-medium rounded-md transition-colors duration-200 ${
                  isActive(item.path)
                    ? 'bg-gradient-to-r from-blue-900 to-blue-800 text-white'
                    : 'text-gray-300 hover:bg-gray-800 hover:text-white'
                }`}
                onClick={() => window.innerWidth < 768 && setIsOpen(false)}
              >
                <span className="mr-3">{item.icon}</span>
                {item.name}
              </Link>
            ))}
          </nav>

          {/* Status Indicator */}
          <div className="p-4 border-t border-gray-800">
            <div className="flex items-center">
              <div className="w-2 h-2 mr-2 bg-green-500 rounded-full"></div>
              <span className="text-sm font-medium text-green-400">System Active</span>
            </div>
            <div className="mt-2 text-xs text-gray-500">Last update: {new Date().toLocaleTimeString()}</div>
          </div>

          {/* Settings and Logout */}
          <div className="p-4 border-t border-gray-800">
            <button
              onClick={() => setIsSettingsOpen(!isSettingsOpen)}
              className="flex items-center justify-between w-full px-4 py-2 text-sm font-medium text-gray-300 rounded-md hover:bg-gray-800 hover:text-white"
            >
              <div className="flex items-center">
                <Settings className="w-5 h-5 mr-3" />
                Settings
              </div>
              <ChevronDown
                className={`w-4 h-4 transition-transform duration-200 ${
                  isSettingsOpen ? 'transform rotate-180' : ''
                }`}
              />
            </button>

            {/* Settings Dropdown */}
            {isSettingsOpen && (
              <div className="py-2 mt-2 space-y-1 text-sm">
                <button className="flex w-full items-center px-4 py-2 text-gray-400 rounded-md hover:bg-gray-800 hover:text-white">
                  <User className="w-4 h-4 mr-3" />
                  Profile
                </button>
                <button className="flex w-full items-center px-4 py-2 text-gray-400 rounded-md hover:bg-gray-800 hover:text-white">
                  <Bell className="w-4 h-4 mr-3" />
                  Notifications
                </button>
                <button className="flex w-full items-center px-4 py-2 text-red-400 rounded-md hover:bg-red-900/30 hover:text-red-300">
                  <LogOut className="w-4 h-4 mr-3" />
                  Logout
                </button>
              </div>
            )}
          </div>
        </div>
      </div>

      {/* Overlay to close menu on mobile */}
      {isOpen && (
        <div
          className="fixed inset-0 z-20 bg-black bg-opacity-50 md:hidden"
          onClick={() => setIsOpen(false)}
        ></div>
      )}
    </>
  );
};

export default Navbar;
