import React, { useState, useEffect, createContext, Suspense } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Navbar from './components/navbar';
import Dashboard from './components/dashboard';
import Analysis from './components/analysis';
import Security from './components/security';
import { Shield, AlertTriangle } from 'lucide-react';

// Add base URL for API calls
export const API_BASE_URL = process.env.REACT_APP_API_BASE_URL || 'http://localhost:5000';

// Create API context for global access to API functions
export const ApiContext = createContext();

// Error Boundary Component
class ErrorBoundary extends React.Component {
  constructor(props) {
    super(props);
    this.state = { hasError: false, error: null, errorInfo: null };
  }

  static getDerivedStateFromError(error) {
    return { hasError: true };
  }

  componentDidCatch(error, errorInfo) {
    console.error("Error caught by boundary:", error, errorInfo);
    this.setState({ error, errorInfo });
  }

  render() {
    if (this.state.hasError) {
      return (
        <div className="min-h-screen bg-gray-900 flex flex-col items-center justify-center p-4 text-white">
          <AlertTriangle className="text-red-500 w-16 h-16 mb-4" />
          <h1 className="text-2xl font-bold mb-2">Something went wrong</h1>
          <p className="text-gray-400 mb-4 text-center max-w-md">
            An error occurred in the application. You can try refreshing the page or contact support if the issue persists.
          </p>
          <button 
            onClick={() => window.location.reload()} 
            className="px-4 py-2 bg-blue-600 hover:bg-blue-700 rounded-lg transition-colors"
          >
            Refresh Page
          </button>
          <div className="mt-8 p-4 bg-gray-800 rounded-lg max-w-lg overflow-auto">
            <p className="text-red-400 text-sm font-mono">
              {this.state.error && this.state.error.toString()}
            </p>
          </div>
        </div>
      );
    }

    return this.props.children;
  }
}

// Loading Spinner Component
const LoadingSpinner = () => (
  <div className="min-h-screen bg-gray-900 flex flex-col items-center justify-center">
    <div className="w-24 h-24 bg-blue-900 rounded-full flex items-center justify-center mb-6">
      <Shield className="w-14 h-14 text-cyan-400" />
    </div>
    <div className="text-xl font-bold text-cyan-400 mb-2">Loading...</div>
    <div className="w-8 h-8 border-4 border-blue-600 border-t-transparent rounded-full animate-spin"></div>
  </div>
);

const App = () => {
  const [isReady, setIsReady] = useState(false);
  const [apiHealth, setApiHealth] = useState({ status: 'unknown', message: null });

  // API helper functions
  const apiHelpers = {
    // Function to perform GET requests
    get: async (endpoint) => {
      try {
        const response = await fetch(`${API_BASE_URL}${endpoint}`);
        if (!response.ok) {
          throw new Error(`API Error: ${response.status}`);
        }
        return await response.json();
      } catch (error) {
        console.error(`GET Error for ${endpoint}:`, error);
        throw error;
      }
    },
    
    // Function to perform POST requests
    post: async (endpoint, data) => {
      try {
        const response = await fetch(`${API_BASE_URL}${endpoint}`, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify(data)
        });
        if (!response.ok) {
          throw new Error(`API Error: ${response.status}`);
        }
        return await response.json();
      } catch (error) {
        console.error(`POST Error for ${endpoint}:`, error);
        throw error;
      }
    },
    
    // Health check to see if API is available
    checkHealth: async () => {
      try {
        const response = await fetch(`${API_BASE_URL}/api/health`, { method: 'GET' });
        if (response.ok) {
          const data = await response.json();
          setApiHealth({ status: 'healthy', message: data.status });
          return true;
        } else {
          setApiHealth({ status: 'unhealthy', message: `HTTP ${response.status}` });
          return false;
        }
      } catch (error) {
        setApiHealth({ status: 'unavailable', message: error.message });
        return false;
      }
    }
  };

  useEffect(() => {
    // Check API health when app loads
    apiHelpers.checkHealth();
    
    // Simulate app initialization (you could load initial required data here)
    const initTimer = setTimeout(() => {
      setIsReady(true);
    }, 1000); // Show loading screen for at least 1 second
    
    return () => clearTimeout(initTimer);
  }, []);

  if (!isReady) {
    return <LoadingSpinner />;
  }

  return (
    <ApiContext.Provider value={apiHelpers}>
      <ErrorBoundary>
        <Router>
          <div className="bg-gray-900 min-h-screen flex flex-col md:flex-row">
            <Navbar />
            
            {/* API Status banner - only show when API is not healthy */}
            {apiHealth.status !== 'healthy' && apiHealth.status !== 'unknown' && (
              <div className="bg-red-900 text-white py-2 px-4 text-sm text-center">
                API connection issue: {apiHealth.message || 'Unable to connect to backend services'}. 
                Some features may be unavailable.
                <button 
                  onClick={apiHelpers.checkHealth}
                  className="ml-4 underline hover:text-cyan-300"
                >
                  Retry connection
                </button>
              </div>
            )}
            
            <div className="flex-1">
              <Suspense fallback={<LoadingSpinner />}>
                <Routes>
                  <Route path="/" element={<Navigate to="/dashboard" replace />} />
                  <Route path="/dashboard" element={<Dashboard />} />
                  <Route path="/analysis" element={<Analysis />} />
                  <Route path="/security" element={<Security />} />
                  <Route path="*" element={
                    <div className="min-h-screen bg-gray-900 flex items-center justify-center text-white">
                      <div className="text-center">
                        <h2 className="text-3xl font-bold mb-2">Page Not Found</h2>
                        <p className="text-gray-400 mb-6">The page you're looking for doesn't exist or has been moved.</p>
                        <button 
                          onClick={() => window.history.back()}
                          className="px-4 py-2 bg-blue-600 hover:bg-blue-700 rounded-lg transition-colors"
                        >
                          Go Back
                        </button>
                      </div>
                    </div>
                  } />
                </Routes>
              </Suspense>
            </div>
          </div>
        </Router>
      </ErrorBoundary>
    </ApiContext.Provider>
  );
};

export default App;
