import React, { useState, useEffect } from 'react';
import { Shield, AlertTriangle, Lock, CheckCircle, X, ExternalLink, Eye, EyeOff, Clock, Server, Database, User } from 'lucide-react';
import { API_BASE_URL } from '../app';

const Security = () => {
  const [securityData, setSecurityData] = useState(null);
  const [threatLog, setThreatLog] = useState([]);
  const [vulnerabilities, setVulnerabilities] = useState([]);
  const [isScanning, setIsScanning] = useState(false);
  const [lastScanTime, setLastScanTime] = useState(null);

  useEffect(() => {
    fetchSecurityData();
  }, []);

  const fetchSecurityData = async () => {
    try {
      // Using the correct API endpoints from our backend
      const [securityRes, threatRes, vulnRes] = await Promise.all([
        fetch(`${API_BASE_URL}/api/security/overview`),
        fetch(`${API_BASE_URL}/api/threats`),
        fetch(`${API_BASE_URL}/api/security/vulnerabilities`)
      ]);
      
      const securityData = await securityRes.json();
      const threatData = await threatRes.json();
      const vulnData = await vulnRes.json();
      
      // Handle the data
      setSecurityData(securityData);
      setThreatLog(threatData);
      setVulnerabilities(vulnData);
      setLastScanTime(new Date().toISOString());
    } catch (error) {
      console.error('Error fetching security data:', error);
    }
  };

  const startSecurityScan = async () => {
    setIsScanning(true);
    
    try {
      // Start a new analysis session through the API
      await fetch(`${API_BASE_URL}/api/start_analysis`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          count: 100,  // Capture 100 packets
          duration: 10  // For 10 seconds
        })
      });
      
      // Wait for the scan to complete
      setTimeout(async () => {
        // Refresh security data after scan
        await fetchSecurityData();
        setIsScanning(false);
      }, 3000);
    } catch (error) {
      console.error('Error during security scan:', error);
      setIsScanning(false);
    }
  };

  const getSeverityColor = (severity) => {
    switch (severity.toLowerCase()) {
      case 'critical': return 'text-red-500';
      case 'high': return 'text-orange-500';
      case 'medium': return 'text-yellow-500';
      case 'low': return 'text-green-500';
      default: return 'text-gray-500';
    }
  };

  const getSeverityBg = (severity) => {
    switch (severity.toLowerCase()) {
      case 'critical': return 'bg-red-900 border-red-500';
      case 'high': return 'bg-orange-900 border-orange-500';
      case 'medium': return 'bg-yellow-900 border-yellow-500';
      case 'low': return 'bg-green-900 border-green-500';
      default: return 'bg-gray-800 border-gray-500';
    }
  };

  // Format timestamp to readable date/time
  const formatDate = (timestamp) => {
    if (!timestamp) return 'N/A';
    
    try {
      return new Date(timestamp).toLocaleString();
    } catch (e) {
      return timestamp;
    }
  };

  return (
    <div className="min-h-screen bg-gray-900 text-white pt-6">
      <div className="container mx-auto px-4">
        <div className="flex justify-between items-center mb-8">
          <div>
            <h1 className="text-3xl font-bold text-cyan-400">Security Center</h1>
            <p className="text-gray-400 mt-1">Network security monitoring and threat prevention</p>
          </div>
          <button
            className={`flex items-center space-x-2 bg-blue-700 hover:bg-blue-600 text-white px-4 py-2 rounded-lg transition-all ${isScanning ? 'opacity-75 cursor-not-allowed' : ''}`}
            onClick={startSecurityScan}
            disabled={isScanning}
          >
            {isScanning ? (
              <>
                <Shield className="w-5 h-5 animate-spin" />
                <span>Scanning...</span>
              </>
            ) : (
              <>
                <Shield className="w-5 h-5" />
                <span>Run Security Scan</span>
              </>
            )}
          </button>
        </div>

        {/* Security Overview Cards */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
          <div className="bg-gray-800 rounded-lg p-6 border border-gray-700">
            <div className="flex justify-between items-start">
              <div>
                <h3 className="text-gray-400 text-sm">Security Score</h3>
                <p className="text-3xl font-bold mt-2 text-cyan-400">{securityData?.security_score || '85'}/100</p>
              </div>
              <Shield className="w-8 h-8 text-cyan-400" />
            </div>
            <div className="mt-4">
              <div className="w-full bg-gray-700 rounded-full h-2.5">
                <div className="bg-cyan-400 h-2.5 rounded-full" style={{ width: `${securityData?.security_score || 85}%` }}></div>
              </div>
            </div>
          </div>
          
          <div className="bg-gray-800 rounded-lg p-6 border border-gray-700">
            <div className="flex justify-between items-start">
              <div>
                <h3 className="text-gray-400 text-sm">Active Threats</h3>
                <p className="text-3xl font-bold mt-2 text-red-400">{securityData?.active_threats || '3'}</p>
              </div>
              <AlertTriangle className="w-8 h-8 text-red-400" />
            </div>
            <p className="mt-2 text-sm text-gray-400">Last detected: {formatDate(securityData?.last_threat_time)}</p>
          </div>
          
          <div className="bg-gray-800 rounded-lg p-6 border border-gray-700">
            <div className="flex justify-between items-start">
              <div>
                <h3 className="text-gray-400 text-sm">Vulnerabilities</h3>
                <p className="text-3xl font-bold mt-2 text-orange-400">{securityData?.vulnerabilities_count || '7'}</p>
              </div>
              <Lock className="w-8 h-8 text-orange-400" />
            </div>
            <div className="mt-2 text-sm">
              <span className="text-red-400">2 Critical</span>
              <span className="text-orange-400 ml-2">5 Medium</span>
            </div>
          </div>
          
          <div className="bg-gray-800 rounded-lg p-6 border border-gray-700">
            <div className="flex justify-between items-start">
              <div>
                <h3 className="text-gray-400 text-sm">Protected Assets</h3>
                <p className="text-3xl font-bold mt-2 text-green-400">{securityData?.protected_assets || '12'}</p>
              </div>
              <Server className="w-8 h-8 text-green-400" />
            </div>
            <p className="mt-2 text-sm text-gray-400">Last scan: {lastScanTime ? formatDate(lastScanTime) : 'Never'}</p>
          </div>
        </div>

        {/* Main Content - Two Columns */}
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
          {/* Threat Log - 2/3 width */}
          <div className="lg:col-span-2 bg-gray-800 rounded-lg p-6 border border-gray-700">
            <h3 className="text-xl font-bold mb-4 text-cyan-400 flex items-center">
              <AlertTriangle className="w-5 h-5 mr-2" />
              Recent Threat Activity
            </h3>
            
            <div className="space-y-4 max-h-96 overflow-y-auto pr-2">
              {threatLog && threatLog.length > 0 ? (
                threatLog.map((threat, index) => (
                  <div 
                    key={index} 
                    className={`p-4 rounded-lg border ${getSeverityBg(threat.severity)}`}
                  >
                    <div className="flex justify-between mb-2">
                      <h4 className="font-semibold">{threat.type}</h4>
                      <span className={`text-sm ${getSeverityColor(threat.severity)}`}>
                        {threat.severity}
                      </span>
                    </div>
                    <div className="grid grid-cols-2 text-sm text-gray-400 mb-2">
                      <p>Source IP: <span className="text-cyan-300">{threat.source_ip}</span></p>
                      <p>Time: {formatDate(threat.timestamp)}</p>
                    </div>
                    <p className="text-sm">{threat.details}</p>
                  </div>
                ))
              ) : (
                <div className="text-center text-gray-500 py-8">
                  <AlertTriangle className="w-12 h-12 mx-auto mb-4 opacity-30" />
                  <p>No recent threats detected</p>
                </div>
              )}
            </div>
          </div>

          {/* Vulnerabilities Panel - 1/3 width */}
          <div className="bg-gray-800 rounded-lg p-6 border border-gray-700">
            <h3 className="text-xl font-bold mb-4 text-cyan-400 flex items-center">
              <Lock className="w-5 h-5 mr-2" />
              Vulnerability Assessment
            </h3>
            
            <div className="space-y-3 max-h-96 overflow-y-auto pr-2">
              {vulnerabilities && vulnerabilities.length > 0 ? (
                vulnerabilities.map((vuln, index) => (
                  <div 
                    key={index} 
                    className="p-4 bg-gray-900 rounded-lg border border-gray-700 hover:border-cyan-500 transition-all"
                  >
                    <div className="flex justify-between items-start">
                      <h4 className={`font-medium ${getSeverityColor(vuln.severity)}`}>
                        {vuln.cve_id || `VULN-${index + 1}`}
                      </h4>
                      <span className={`text-xs px-2 py-1 rounded ${getSeverityBg(vuln.severity)}`}>
                        {vuln.severity}
                      </span>
                    </div>
                    <p className="text-sm my-2">{vuln.description}</p>
                    <div className="flex justify-between text-xs text-gray-400">
                      <span>Affected: {vuln.affected_component}</span>
                      <span>Status: {vuln.status}</span>
                    </div>
                    {vuln.remediation && (
                      <div className="mt-2 text-xs text-green-400">
                        <strong>Remediation:</strong> {vuln.remediation}
                      </div>
                    )}
                  </div>
                ))
              ) : (
                <div className="text-center text-gray-500 py-8">
                  <Lock className="w-12 h-12 mx-auto mb-4 opacity-30" />
                  <p>No vulnerabilities found</p>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Security;
